package com.grow.java8.tickets.service.impl;import com.grow.java8.tickets.dao.TicketDAO;import com.grow.java8.tickets.data.Ticket;import com.grow.java8.tickets.service.TicketService;import java.util.ArrayList;import java.util.List;import java.util.Optional;public abstract class TicketServiceImpl implements TicketService {    public Ticket sellTicket(String Buyer) {        Optional<Ticket> ticket = getTicketDAO().getLastFree();        ticket.get().setBuyer(Buyer);        pause(6000);        getTicketDAO().setTicket(ticket.get());                return ticket.get();    }    public List<Long> getCountSold() {        List<Long> result = new ArrayList<>();        result.add(getTicketDAO().getCuntSold());        pause(15000);        result.add(getTicketDAO().getCuntSold());        return result;    }    private void pause(long millis) {        try {            Thread.sleep(millis);        } catch (InterruptedException e) {            e.printStackTrace();        }    }    protected abstract TicketDAO getTicketDAO();}