package com.grow.java8.tickets.service.impl;import java.util.List;import com.grow.java8.tickets.dao.TicketDAO;import com.grow.java8.tickets.data.Ticket;import com.grow.java8.tickets.service.TicketService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Isolation;import org.springframework.transaction.annotation.Transactional;@Service("ticketServiceReadCommited")public class TicketServiceReadCommitedImpl extends TicketServiceImpl implements TicketService {    @Autowired    TicketDAO ticketDAO;    @Override    @Transactional(isolation=Isolation.READ_COMMITTED)    public Ticket sellTicket(String Buyer) {        return super.sellTicket(Buyer);    }    @Override    @Transactional(isolation=Isolation.READ_COMMITTED)    public List<Long> getCountSold() {        return super.getCountSold();    }    private void pause(long millis) {        try {            Thread.sleep(millis);        } catch (InterruptedException e) {            e.printStackTrace();        }    }    @Override    protected TicketDAO getTicketDAO() {        return ticketDAO;    }}