package com.grow.java8.tickets.service.impl;import com.grow.java8.tickets.dao.TicketDAO;import com.grow.java8.tickets.data.Ticket;import com.grow.java8.tickets.service.TicketService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Isolation;import org.springframework.transaction.annotation.Transactional;@Service("ticketServiceReadCommited")public class TicketServiceReadCommitedImpl extends TicketServiceImpl implements TicketService {    @Autowired    TicketDAO ticketDAO;    @Override    @Transactional(isolation=Isolation.READ_COMMITTED)    public Ticket sellTicket(String buyer) {        return super.sellTicket(buyer);    }    @Override    @Transactional(isolation=Isolation.READ_COMMITTED)    public Long getCountSold() {        return super.getCountSold();    }        @Override    protected TicketDAO getTicketDAO() {        return ticketDAO;    }}