package com.grow.java8.tickets.service.impl;import com.grow.java8.tickets.dao.TicketDAO;import com.grow.java8.tickets.data.Ticket;import com.grow.java8.tickets.service.TicketService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Isolation;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Service("ticketServiceSerializable")public class TicketServiceSerializableImpl extends TicketServiceImpl implements TicketService {    @Autowired    TicketDAO ticketDAO;    @Override    @Transactional(isolation=Isolation.SERIALIZABLE)    public Ticket sellTicket(String Buyer) {        return super.sellTicket(Buyer);    }    @Override    @Transactional(isolation=Isolation.SERIALIZABLE)    public List<Long> getCountSold() {        return super.getCountSold();    }    @Override    protected TicketDAO getTicketDAO() {        return ticketDAO;    }}