package com.grow.java8.tickets.service.impl;import com.grow.java8.tickets.dao.TicketDAO;import com.grow.java8.tickets.data.Ticket;import com.grow.java8.tickets.enums.TimePoint;import com.grow.java8.tickets.service.TicketService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Isolation;import org.springframework.transaction.annotation.Propagation;import org.springframework.transaction.annotation.Transactional;import java.util.Map;@Service("ticketServiceSerializable")public class TicketServiceSerializableImpl extends TicketServiceImpl implements TicketService {    @Autowired    TicketDAO ticketDAO;    @Override    @Transactional(isolation=Isolation.SERIALIZABLE, propagation = Propagation.REQUIRES_NEW)    public Ticket sellTicket(String buyer) {        return super.sellTicket(buyer);    }    @Override    @Transactional(isolation=Isolation.SERIALIZABLE, propagation = Propagation.REQUIRES_NEW)    public Map<TimePoint, Long> getCountSold() {        return super.getCountSold();    }    @Override    @Transactional(isolation=Isolation.SERIALIZABLE, propagation = Propagation.REQUIRES_NEW)    public Ticket getTicket(String id) {        return super.getTicket(id);    }    @Override    @Transactional(isolation=Isolation.SERIALIZABLE, propagation = Propagation.REQUIRES_NEW)    public Ticket setBuyer(String id, String buyer) {        return super.setBuyer(id, buyer);    }    @Override    protected TicketDAO getTicketDAO() {        return ticketDAO;    }}